import * as pulumi from "@pulumi/pulumi";
import * as github from "@pulumi/github";

const exampleTemplate = new github.ActionsOrganizationOidcSubjectClaimCustomizationTemplate("example_template", {includeClaimKeys: [
    "actor",
    "context",
    "repository_owner",
]});
