    username: "SomeUser",
    role: "member",
});
const someTeam = new github.Team("some_team", {
    name: "SomeTeam",
    description: "Some cool team",
});
const someTeamMembership = new github.TeamMembership("some_team_membership", {
    teamId: someTeam.id,
    username: "SomeUser",
    role: "member",
});
