        Repository = @this.Apply(getRepositoryResult => getRepositoryResult.Name),
    });

    var thisGetTree = Github.GetTree.Invoke(new()
    {
        Recursive = false,
        Repository = @this.Apply(getRepositoryResult => getRepositoryResult.Name),
