```
```csharp
using System.IO;
using Pulumi;
using Github = Pulumi.Github;

class MyStack : Stack
{
    public MyStack()
    {
        var example = new Github.UserSshKey("example", new Github.UserSshKeyArgs
        {
            Title = "example title",
            Key = File.ReadAllText("~/.ssh/id_rsa.pub"),
        });
    }

}
```
```go
package main

import (
	"io/ioutil"

	"github.com/pulumi/pulumi-github/sdk/v4/go/github"
	"github.com/pulumi/pulumi/sdk/v3/go/pulumi"
)

func readFileOrPanic(path string) pulumi.StringPtrInput {
	data, err := ioutil.ReadFile(path)
	if err != nil {
		panic(err.Error())
	}
	return pulumi.String(string(data))
}

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		_, err := github.NewUserSshKey(ctx, "example", &github.UserSshKeyArgs{
			Title: pulumi.String("example title"),
			Key:   readFileOrPanic("~/.ssh/id_rsa.pub"),
		})
		if err != nil {
			return err
		}
		return nil
	})
}
```
